import { House } from './../Models/house.model';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { HomesApiService } from '..//Services/homes-api.service'; 
import { Apartment } from '../Models/apartment.model'; 
import { Injectable } from '@angular/core';
import { AuthorizeService } from 'src/api-authorization/authorize.service';
import jwtDecode from 'jwt-decode';
@Injectable({
  providedIn: 'root',
})
@Component({
  selector: 'app-house-detail',
  templateUrl: './house-detail.component.html',
  styleUrls: ['./house-detail.component.css']
})
export class HouseDetailComponent implements OnInit {
  houseId: number | undefined;
  isManager: boolean = false;
  apartmentId: number | undefined;
  isResident: boolean = false;
  house: House | undefined;
  apartments: Apartment[]| undefined;
  showEditForm: boolean | undefined;
  housedetails:House={
    id: 0,
    number: 0,
    street: '',
    city: '',
    country: '',
    postcode: '',
  }
  constructor(
    private route: ActivatedRoute,
    private router:Router, 
    private houseService: HomesApiService,
    private AuthorizeService: AuthorizeService

  ) { }
  ngOnInit(): void {
    this.manager();
    this.route.params.subscribe(params => {
      this.houseId = +params['id']; 
      this.getHouseDetails(this.houseId).subscribe({
        next: (response: House) => {
          this.housedetails = response;
        }
      });
      this.getApartmentsByHouseId(this.houseId);
    });
  }
  navigateToApartmentResidents(apartmentId: number) {
   console.log(apartmentId);
  }


  getHouseDetails(houseId: number) {
    return this.houseService.getHouseById(houseId);
  }


  getApartmentsByHouseId(houseId: number) {
    this.houseService.GetHouseApartments(houseId).subscribe(
      (apartments: Apartment[]) => {
        this.apartments = apartments;
      },
      (error) => {
        console.error('Error fetching apartments:', error);
      }
    );
  }
  updateHouse(){
    this.houseService.UpdateHouse(this.housedetails.id,this.housedetails).subscribe({
      next:(response)=>{
        this.router.navigate(['all-houses'])
      }
    })
  }
  deleteHouse(houseId: number){
    this.houseService.DeleteHouse(houseId).subscribe({
      next:(response)=>{
        this.router.navigate(['all-houses'])
      }
    })
  }
  addHouse(house:House){
    console.log(house);
    this.houseService.CreateHouse(this.housedetails).subscribe({
      next:(house)=>{
        console.log(house);
        this.router.navigate(['all-houses'])
      }
    })
  }

  manager(): void {
    this.AuthorizeService.getAccessToken().subscribe((userRole: string | null) => {
      if (userRole !== null) {
        const token: any = jwtDecode(userRole);
        const role = token.role;

        this.isManager = role === 'Manager';
        this.isResident = role === 'Resident';
      } else {
        this.isManager = false;
        this.isResident = false;
      }
    });
  }


}
<div class="container">
  <h2>House</h2>
  <div class="">
    <form #form="ngForm">
      <div class="form-group row ">
      <div class="col-sm-3 ">
        <label for="id" class="form-label">Id</label>
        <input type="number" class="form-control form-control-sm" id="id" name="id" [(ngModel)]="housedetails.id">
      </div>
      <div class="col-sm-3 ">
        <label for="number" class="form-label">Number</label>
        <input type="number" class="form-control form-control-sm" id="number" name="number" [(ngModel)]="housedetails.number">
      </div>
      <div class="col-sm-3 ">
        <label for="street" class="form-label">Street</label>
        <input type="text" class="form-control form-control-sm" id="street" name="street" [(ngModel)]="housedetails.street">
      </div>
      <div class="col-sm-3 ">
        <label for="city" class="form-label">City</label>
        <input type="text" class="form-control form-control-sm" id="city" name="city" [(ngModel)]="housedetails.city">
      </div>
      <div class="col-sm-3 ">
        <label for="country" class="form-label">Country</label>
        <input type="text" class="form-control form-control-sm" id="country" name="country" [(ngModel)]="housedetails.country">
      </div>
      <div class="col-sm-3 ">
        <label for="postcode" class="form-label">Postcode</label>
        <input type="text" class="form-control form-control-sm" id="postcode" name="postcode" [(ngModel)]="housedetails.postcode">
      </div>
      <div class="row-sm-3">
        <button class="btn btn-warning"(click)="updateHouse()">Save</button>
        <button class="btn btn-danger" (click)="deleteHouse(housedetails.id)">Delete</button>
        <button class="btn btn-info" (click)="addHouse(housedetails)">Create</button>
      </div>
    </div>
    </form>

  </div>


</div>



<div class="container">
    <h2>All Apartments</h2>
    <table class="table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Number</th>
          <th>Floor</th>
          <th>Number of Rooms</th>
          <th>Population</th>
          <th>Full Area</th>
          <th>Living Space</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let apartment of apartments">
          <td>{{ apartment.id }}</td>
          <td>{{ apartment.number }}</td>
          <td>{{ apartment.floor }}</td>
          <td>{{ apartment.numberOfRooms }}</td>
          <td>{{ apartment.population }}</td>
          <td>{{ apartment.fullArea }}</td>
          <td>{{ apartment.livingSpace }}</td>
          <!-- Use [routerLink] to navigate to residents of the specific apartment -->
          <td><a class="btn btn-success" [routerLink]="['/apartments', apartment.id, 'residents']">View</a></td>
        </tr>
      </tbody>
    </table>
  </div>
